static void print_status_narrow(void) {
    switch (get_highest_layer(layer_state)) {
        case 0:
        case 1:
            oled_write_raw_P(raw_b, sizeof(raw_b));
            break;
        case 2:
            oled_write_raw_P(raw_c, sizeof(raw_c));
            break;
        case 3:
            oled_write_raw_P(raw_d, sizeof(raw_d));
            break;
        default:
            oled_write_raw_P(raw_a, sizeof(raw_a));
    }
}


oled_rotation_t oled_init_user(oled_rotation_t rotation) {
    return OLED_ROTATION_0;
}


bool oled_task_user(void) {
    bool kb_master = is_keyboard_master();

    if (kb_master) {
        print_status_narrow();
    } else {
        render_logo();
    }

    return false;
}

